const INITIAL_STATE = {
  todoList: ['Limpar a cozinha']
}

function todos(state=INITIAL_STATE, action){
  switch(action.type){
    case 'ADD_TODO':
      return{
        ...state,
        todoList: [...state, action.item]
      }
    default: 
    return state
  }
}

export default todos
